Properties.Octopus.Action.Script.ScriptBody:
& net use T: \\$StackResourcesName.file.core.windows.net\dsc /u:$StackResourcesName $StackResourcesKey
Import-AzureRmAutomationDscConfiguration -ResourceGroupName $InfraRg -AutomationAccountName $AutomationAccountName -SourcePath $DSCConfigPath -Force -Published
& net use T: /DELETE

$CompilationJob = Start-AzureRmAutomationDscCompilationJob -ResourceGroupName $InfraRg -AutomationAccountName $AutomationAccountName -ConfigurationName $DSCConfigurationName -Parameters @{
    ApiKey = $APIKey
    OctopusServerUrl = $OctopusHostHeader
}

while ($CompilationJob.EndTime -eq $null -and $CompilationJob.Exception -eq $null)
{
        Write-Host 'Waiting for compilation...'
        Start-Sleep -Seconds 10
        $CompilationJob = $CompilationJob | Get-AzureRmAutomationDscCompilationJob
}

$CompilationJob | Get-AzureRmAutomationDscCompilationJobOutput -Stream Any

$resourceGroup = Get-AzureRmResourceGroup -Name $ResourceGroupName -ErrorAction SilentlyContinue
if(!$resourceGroup) {
    Write-Output "Creating resource group '$ResourceGroupName' in location '$Location'"
    New-AzureRmResourceGroup -Name $ResourceGroupName -Location $Location | Out-Default
}
else {
    Write-Output "Using existing resource group '$ResourceGroupName'"
    $resourceGroup | Out-Default
}

Properties.Octopus.Action.Azure.ResourceGroupTempla:
{
  "$schema": "https://schema.management.azure.com/schemas/2015-01-01/deploymentTemplate.json#",
  "contentVersion": "1.0.0.0",
  "parameters": {
    "infraResourceGroup": {
      "type": "string",
      "metadata": {
        "description": "Name of the resource group containing the core automationstack infrastructure"
      }
    },
    "udp": {
      "type": "string",
      "metadata": {
        "description": "Unique Deployment Prefix to avoid name collisions"
      }
    },
    "productName": {
      "type": "string",
      "metadata": {
        "description": "Name of the product that is running on this VM"
      }
    },
    "vmSize": {
      "type": "string",
      "defaultValue": "Standard_F4s",
      "metadata": {
        "description": "Size of the VM"
      }
    },
    "vmAdminUsername": {
      "type": "string",
      "metadata": {
        "description": "Username for the Virtual Machine."
      }
    },
    "vmAdminPassword": {
      "type": "securestring",
      "metadata": {
        "description": "Password for the Virtual Machine."
      }
    },
    "clientID": {
      "type": "string",
      "metadata": {
        "description": "Client ID of AAD app which has permissions to KeyVault"
      }
    },
    "clientSecret": {
      "type": "securestring",
      "metadata": {
        "description": "Client Secret of AAD app which has permissions to KeyVault"
      }
    },
    "registrationKey": {
      "type": "securestring",
      "metadata": {
        "Description": "Registration key to use to onboard to the Azure Automation DSC pull/reporting server"
      }
    },
    "registrationUrl": {
      "type": "string",
      "metadata": {
        "Description": "Registration url of the Azure Automation DSC pull/reporting server"
      }
    },
    "nodeConfigurationName": {
      "type": "string",
      "metadata": {
        "Description": "The name of the node configuration, on the Azure Automation DSC pull server, that this node to pick up"
      }
    },
    "timestamp": {
      "type": "string",
      "defaultValue": "MM/dd/yyyy H:mm:ss tt",
      "metadata": {
        "description": "The current datetime, as a string, to force the request to go through ARM even if all fields are the same as last ARM deployment of this template; example in parameters file is in MM/dd/yyyy H:mm:ss tt format"
      }
    }
  },
  "variables": {
    "vmName": "[concat(parameters('productName'),'VM')]",
    "vmStorageName": "[concat('vmvhds', parameters('udp'), uniquestring(resourceGroup().id))]",
    "nsgName": "[concat(parameters('productName'),'NSG')]",
    "nicName": "[concat(parameters('productName'),'NIC')]",
    "publicIPAddressName": "[concat(parameters('productName'),'PublicIP')]",
    "virtualNetworkId": "[resourceId(parameters('infraResourceGroup'), 'Microsoft.Network/virtualNetworks', 'VirtualNetwork')]",
    "subnetId": "[concat(variables('virtualNetworkId'),'/subnets/', 'Default')]",
    "keyVaultName": "[concat('keyvault', parameters('udp'))]"
  },
  "resources": [{
    "apiVersion": "2016-01-01",
    "type": "Microsoft.Storage/storageAccounts",
    "name": "[variables('vmStorageName')]",
    "location": "[resourceGroup().location]",
    "sku": {
      "name": "Premium_LRS",
      "tier": "Premium"
    },
    "kind": "Storage",
    "properties": {
      "encryption": {
        "keySource": "Microsoft.Storage",
        "services": {
          "blob": {
            "enabled": true
          }
        }
      }
    }
  }, {
    "apiVersion": "2015-06-15",
    "type": "Microsoft.Network/networkInterfaces",
    "name": "[variables('nicName')]",
    "location": "[resourceGroup().location]",
    "dependsOn": [
      "[concat('Microsoft.Network/publicIPAddresses/', variables('publicIPAddressName'))]"
    ],
    "properties": {
      "ipConfigurations": [{
        "name": "[concat(parameters('productName'),'-IPConfig')]",
        "properties": {
          "privateIPAllocationMethod": "Dynamic",
          "publicIPAddress": {
            "id": "[resourceId('Microsoft.Network/publicIPAddresses/', variables('publicIPAddressName'))]"
          },
          "subnet": {
            "id": "[variables('subnetId')]"
          }
        }
      }],
      "networkSecurityGroup": {
        "id": "[resourceId(parameters('infraResourceGroup'), 'Microsoft.Network/networkSecurityGroups/', variables('nsgName'))]"
      }
    }
  }, {
    "apiVersion": "2015-05-01-preview",
    "type": "Microsoft.Network/publicIPAddresses",
    "name": "[variables('publicIPAddressName')]",
    "location": "[resourceGroup().location]",
    "properties": {
      "publicIPAllocationMethod": "Static",
      "idleTimeoutInMinutes": 30,
      "dnsSettings": {
        "domainNameLabel": "[toLower(concat(parameters('productName'),'stack-',parameters('udp')))]"
      }
    }
  }, {
    "apiVersion": "2015-06-15",
    "type": "Microsoft.Compute/virtualMachines",
    "name": "[variables('vmName')]",
    "location": "[resourceGroup().location]",
    "dependsOn": [
      "[concat('Microsoft.Storage/storageAccounts/', variables('vmStorageName'))]",
      "[concat('Microsoft.Network/networkInterfaces/', variables('nicName'))]"
    ],
    "properties": {
      "hardwareProfile": {
        "vmSize": "[parameters('vmSize')]"
      },
      "osProfile": {
        "computerName": "[variables('vmName')]",
        "adminUsername": "[parameters('vmAdminUsername')]",
        "adminPassword": "[parameters('vmAdminPassword')]",
        "windowsConfiguration": {
          "provisionVMAgent": true,
          "enableAutomaticUpdates": true
        }
      },
      "storageProfile": {
        "imageReference": {
          "publisher": "MicrosoftWindowsServer",
          "offer": "WindowsServer",
          "sku": "2016-Datacenter",
          "version": "latest"
        },
        "osDisk": {
          "name": "[concat(variables('vmName'),'-OS')]",
          "vhd": {
            "uri": "[concat('http://',variables('vmStorageName'),'.blob.core.windows.net/vhds/',variables('vmName'),'-OS.vhd')]"
          },
          "caching": "ReadWrite",
          "createOption": "FromImage",
          "DiskSizeGB": "256"
        }
      },
      "networkProfile": {
        "networkInterfaces": [{
          "id": "[resourceId('Microsoft.Network/networkInterfaces',variables('nicName'))]"
        }]
      }
    },
    "resources": [{
      "type": "extensions",
      "name": "AzureDiskEncryption",
      "apiVersion": "2016-03-30",
      "location": "[resourceGroup().location]",
      "properties": {
        "publisher": "Microsoft.Azure.Security",
        "type": "AzureDiskEncryption",
        "typeHandlerVersion": "1.1",
        "autoUpgradeMinorVersion": true,
        "settings": {
          "AADClientID": "[parameters('clientId')]",
          "KeyVaultURL": "[concat('https://', variables('keyVaultName'), '.vault.azure.net/')]",
          "KeyEncryptionAlgorithm": "RSA-OAEP",
          "VolumeType": "All",
          "EncryptionOperation": "EnableEncryption"
        },
        "protectedSettings": {
          "AADClientSecret": "[parameters('clientSecret')]"
        }
      },
      "dependsOn": [
        "[concat('Microsoft.Compute/virtualMachines/', variables('vmName'))]"
      ]
    }, {
      "type": "extensions",
      "name": "Microsoft.Powershell.DSC",
      "apiVersion": "2015-06-15",
      "location": "[resourceGroup().location]",
      "properties": {
        "publisher": "Microsoft.Powershell",
        "type": "DSC",
        "typeHandlerVersion": "2.19",
        "autoUpgradeMinorVersion": true,
        "protectedSettings": {
          "Items": {
            "registrationKeyPrivate": "[parameters('registrationKey')]"
          }
        },
        "settings": {
          "ModulesUrl": "https://eus2oaasibizamarketprod1.blob.core.windows.net/automationdscpreview/RegistrationMetaConfigV2.zip",
          "SasToken": "",
          "ConfigurationFunction": "RegistrationMetaConfigV2.ps1\\RegistrationMetaConfigV2",
          "Properties": [{
            "Name": "RegistrationKey",
            "Value": {
              "UserName": "PLACEHOLDER_DONOTUSE",
              "Password": "PrivateSettingsRef:registrationKeyPrivate"
            },
            "TypeName": "System.Management.Automation.PSCredential"
          }, {
            "Name": "RegistrationUrl",
            "Value": "[parameters('registrationUrl')]",
            "TypeName": "System.String"
          }, {
            "Name": "NodeConfigurationName",
            "Value": "[parameters('nodeConfigurationName')]",
            "TypeName": "System.String"
          }, {
            "Name": "ConfigurationMode",
            "Value": "ApplyAndAutocorrect",
            "TypeName": "System.String"
          }, {
            "Name": "ConfigurationModeFrequencyMins",
            "Value": 15,
            "TypeName": "System.Int32"
          }, {
            "Name": "RefreshFrequencyMins",
            "Value": 30,
            "TypeName": "System.Int32"
          }, {
            "Name": "RebootNodeIfNeeded",
            "Value": true,
            "TypeName": "System.Boolean"
          }, {
            "Name": "ActionAfterReboot",
            "Value": "ContinueConfiguration",
            "TypeName": "System.String"
          }, {
            "Name": "AllowModuleOverwrite",
            "Value": true,
            "TypeName": "System.Boolean"
          }, {
            "Name": "Timestamp",
            "Value": "[parameters('timestamp')]",
            "TypeName": "System.String"
          }]
        }
      },
        "dependsOn": [
          "[concat('Microsoft.Compute/virtualMachines/', variables('vmName'))]"
        ]
    }]
  }],
  "outputs": {
    "fqdn": {
      "type": "string",
      "value": "[reference(variables('publicIPAddressName')).dnsSettings.fqdn]"
    },
    "keyVaultSecretUrl": {
      "type": "string",
      "value": "[reference(resourceId('Microsoft.Compute/virtualMachines/extensions',  variables('vmName'),  'AzureDiskEncryption')).instanceView.statuses[0].message]"
    }
  }
}

Properties.Octopus.Action.Script.ScriptBody:
while ($continueToPoll)
{
    Start-Sleep -Seconds 30
    $node = Get-AzureRmAutomationDscNode -ResourceGroupName $InfraRg -AutomationAccountName $AutomationAccountName -Name $VMName
    if ($node.Status -eq 'Compliant') {
        Write-Host "Node is compliant"
        $continueToPoll = $false
    }
    else {
        Write-Host "Node status is $($node.Status), waiting for compliance..."
    }
}

Properties.Octopus.Action.Azure.ResourceGroupTempla:
{
    "$schema": "https://schema.management.azure.com/schemas/2015-01-01/deploymentTemplate.json#",
    "contentVersion": "1.0.0.0",
    "parameters": {
        "productName": {
            "type": "string",
            "metadata": {
                "description": "Name of the product that is running on this VM"
            }
        },
        "keyVaultResourceID": {
            "type": "string",
            "metadata": {
                "description": "KeyVault resource id. Ex: /subscriptions/9135e259-1f76-4dbd-a5c8-bc4fcdf3cf1c/resourceGroups/DiskEncryptionTest/providers/Microsoft.KeyVault/vaults/DiskEncryptionTestAus"
            }
        },
        "keyVaultSecretUrl": {
            "type": "string",
            "metadata": {
                "description": "KeyVault secret Url. Ex: https://diskencryptiontestaus.vault.azure.net/secrets/BitLockerEncryptionSecretWithKek/e088818e865e48488cf363af16dea596"
            }
        }
    },
    "variables": {
        "vmName": "[concat(parameters('productName'),'VM')]"
    },
    "resources": [{
        "apiVersion": "2015-06-15",
        "type": "Microsoft.Compute/virtualMachines",
        "name": "[variables('vmName')]",
        "location": "[resourceGroup().location]",
        "properties": {
            "storageProfile": {
                "osDisk": {
                    "encryptionSettings": {
                        "diskEncryptionKey": {
                            "sourceVault": {
                                "id": "[parameters('keyVaultResourceID')]"
                            },
                            "secretUrl": "[parameters('keyVaultSecretUrl')]"
                        }
                    }
                }
            }
        }
    }]
}

Properties.Octopus.Action.Script.ScriptBody:
